import { useState } from 'react';

export default () => {
  const [value, setValue] = useState({name:"", content:""});

  return {
    value,
    onChangeOne: event => {
      setValue( event.target.value.name);
    },
    onChangeTwo: event => {
        setValue( event.target.value.content);
    },
    reset: () => setValue(value)
  };
};
