import React from "react";
import JournalDetail from "./JournalDetail";

const JournalList = () => {
  const productData = [
    {
      id: 1,
      name: "NIKE Liteforce Blue Sneakers",
      description:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Proin molestie.",
      status: "Available"
    },
    {
      id: 2,
      name: "Stylised Flip Flops and Slippers",
      description:
        "Mauris finibus, massa eu tempor volutpat, magna dolor euismod dolor.",
      status: "Out of Stock"
    },
    {
      id: 3,
      name: "ADIDAS Adispree Running Shoes",
      description:
        "Maecenas condimentum porttitor auctor. Maecenas viverra fringilla felis, eu pretium.",
      status: "Available"
    },
    {
      id: 4,
      name: "ADIDAS Mid Sneakers",
      description:
        "Ut hendrerit venenatis lacus, vel lacinia ipsum fermentum vel. Cras.",
      status: "Out of Stock"
    }
  ];

  const { url } = useRouteMatch();
  console.log(useRouteMatch())
  /* Create an array of `<li>` items for each product */
  // Nhận danh sách products
  // duyệt qua từng product
  // Tạo thẻ Link
  // Link này có path là url, rồi biến chạy là product.id (id tăng dần 1, 2, 3, 4)
  const linkList = productData.map((product) => {
    return (
      <li key={product.id}>
        {/* Step 2 - link */}
        <Link to={`${url}/${product.id}`}>{product.name}</Link>
      </li>
    );
  });
  return (
    <div>
      <div className="journal-list-wrapper">
        <header>
          <div className="subTitle">Your entries</div>
          <div className="subTitle">Detail</div>
        </header>
      </div>
      <div className="journal-list-main-area">
        <div className="journal-list">
          <ul>
            <li>go to the beach</li>
            <li>camping</li>
            <li>go to the concert</li>
            <li>play game</li>
          </ul>
        </div>
        <div className="journal-detail">
          <JournalDetail />
        </div>
      </div>
    </div>
  );
};

export default JournalList;
