import { useState } from 'react';

export default () => {
  const [value, setValue] = useState({name:"", content:""});

  return {
    value,
    onChangeOne: event => {
      setValue({...value,name: event.target.value});
    },
    onChangeTwo: event => {
        setValue({...value,content: event.target.value});
    },
    reset: () => setValue({name:"",content:""})
  };
};
