import React from "react";
import { useParams } from "react-router-dom";

const JournalDetail = ({ data }) => {

    const { productId } = useParams();

    // Step 3 - hiển thị thông tin tương ứng id product
    // Nhận toàn bộ mảng product
    // Rồi kiếm sản phẩm trong mảng product
    // Bằng id
    // Kiếm ra thì lưu vô biến product
    // Biến product này hiển thị đúng thông tin của id đó
    const product = data.find((p) => p.id === Number(productId));
    console.log(product);
    let productData;
    if (product) {
      productData = (
        <div>
           <div className="journal-detail-wrapper">
        <div className="journal-detail-form">
          <div className="form-group">
            <label>Title</label>
            <br />
            <input
              className="title-input-field"
              placeholder="Journal Title"
              value={product.name}
            ></input>
          </div>
          <div className="form-group">
            <label>Description</label>
            <br />
            <textarea
              className="description-input-field"
              rows="10"
              placeholder="Journal Description"
              value={product.description}
            ></textarea>
          </div>
          <div>
            <button className="button-save-journal">Save</button>
            <button className="button-save-journal">Delete</button>
          </div>
        </div>
      </div>
      
        </div>
      );
    } else {
      productData = <h2>Sorry. Product doesn't exist</h2>;
    }
    return(
      {productData}
    )
};

export default JournalDetail;
