import React,{useState} from "react";
import { useParams } from "react-router-dom";

const JournalDetail = ({ data,deleTe }) => {
    const { productId } = useParams();
    const product = data.find((p) => p.id === Number(productId));
    let productData;
    const{change,setChange} = useState(false)

    let example = {
      name: "",
      descripsion: ""
    }
  
  
    const [text,setText] = useState({
      name: "",
      description: ""
    });
  
    const reset = () => {
      setText({
        name: "",
      description: ""
      })
    }
  
    useEffect(() => {
      setCheck(false);
    },product)
  
    if ((product != null)) {
      example = product;
    }
  
    
    if (product !== text && (check === false)) {
      setText(product);
      console.log("Da gan lai nhu trc");
      setCheck(true);
    }
  
    const updateTest = () => {
      todos[index] = text;
      console.log("day la todoindex",todos[index]);
      reset();
      setCheck(false);
    }

    if (product) {
     
     productData = (
        <div >
           <div className="journal-detail-wrapper">
              <div className="journal-detail-form">
          <div className="form-group">
            <label>Title</label>
            <br />
            <input type="text" onChange={ (e) => { setChange({...change,name:e.target.value})}}
              className="title-input-field"
              placeholder="Journal Title"
              value={product.name}
             > 
            </input>
          </div>
          <div className="form-group">
            <label>Description</label>
            <br />
            <textarea
              className="description-input-field"
              rows="10"
              placeholder="Journal Description"
              value={product.description}
              >
            </textarea>
          </div>
          <div>
            <button className="button-save-journal" >Save</button>
            <button className="button-save-journal"
              onClick={()=> deleTe(productId)
            }>Delete</button>
          </div>
        </div>
          </div>
         
        </div>
      );
    } else {
      productData = <h2>Sorry. Product doesn't exist</h2>;
     }
    return(
     <div>
       {productData}

      </div>
  )
};



export default JournalDetail;
