import React,{useState} from "react";


const NewJournalForm = () => {
  // const [name, setName] = useState('')
  // const [species, setSpecies] = useState('')
 
  // const handleSubmit = ({e}) => {
  //   e.preventDefault()
  //   props.setPets(prev => prev.concat({name, species}))
  //   setName("")
  //   setSpecies("")
  // }

  return (
    <form onSubmit={handleSubmit}>   
      <div className="new-journal-wrapper">
        <h2>New Journal</h2>

        <div className="new-journal-form">
          <div className="form-group">
            <label>Title</label>
            <br />
            <input
            // value={name} onChange={e => setName(e.target.value)}
              className="title-input-field"
              placeholder="Journal Title"
            ></input>
          </div>
          <div className="form-group">
            <label>Description</label>
            <br />
            <textarea 
            //value={species} onChange={e => setSpecies(e.target.value)}
              className="description-input-field"
              rows="10"
              cols="50"
              placeholder="Journal Description"
            ></textarea>
          </div>
          <div>
            <button className="button-save-journal" >Save</button>
          </div>
        </div>
      </div>
      </form>
  
  );
};


export default NewJournalForm;
