import React,{useState} from "react";
import { useParams } from "react-router-dom";

const JournalDetail = ({ data }) => {
    const { productId } = useParams();
    const product = data.find((p) => p.id === Number(productId));
   // const {journal, setJour} = useState(product);
    const [inputFields, setInputFields] = useState(product);
  
    const handleSubmit = (e) => {
      e.preventDefault();
      console.log("InputFields", inputFields);
    };
  
    const handleChangeInput = (id, event) => {
      const newInputFields = inputFields.map(i => {
        if(id === i.id) {
          i[event.target.name] = event.target.value
        }
        return i;
      })
      
      setInputFields(newInputFields);
    }
    return (
        <div  onSubmit={handleSubmit}>
          { inputFields.map(inputField => (
                <div key={inputField.id}>
               <div>
           <div className="journal-detail-wrapper">
              <div className="journal-detail-form">
          <div className="form-group">
            <label>Title</label>
            <br />
              <input
                name="firstName"
                label="First Name"
                variant="filled"
                value={inputField.firstName}
                onChange={event => handleChangeInput(inputField.id, event)}></input>
              </div>
          <div className="form-group">
            <label>Description</label>
            <br />
            <textarea
                name="lastName"
                label="Last Name"
                variant="filled"
                value={inputField.lastName}
                onChange={event => handleChangeInput(inputField.id, event)}
              >
              </textarea>
          </div>
          <div>
            <button className="button-save-journal" >Save</button>
            <button className="button-save-journal">Delete</button>
          </div>
        </div>
          </div>
        
        </div>
              </div>
              ))};
              </div>
              )
            };
          
    {/* // let productData;
    // if (product) {
    //   productData = (
        <div>
           <div className="journal-detail-wrapper">
              <div className="journal-detail-form">
          <div className="form-group">
            <label>Title</label>
            <br />
            <input type="text" onChange
            //   const name = e.target.value;
            //   setJour(nowJour => 
            //     nowJour.map(x => 
            //       x.id === product.id 
            //       ? {
            //         ...x,
            //         name}
            //       : x 
            //         )
            //       ); 
            // }}
              className="title-input-field"
              placeholder="Journal Title"
              value={product.name}> 
            </input>
          </div>
          <div className="form-group">
            <label>Description</label>
            <br />
            <textarea
              className="description-input-field"
              rows="10"
              placeholder="Journal Description"
              value={product.description}>
            </textarea>
          </div>
          <div>
            <button className="button-save-journal" >Save</button>
            <button className="button-save-journal">Delete</button>
          </div>
        </div>
          </div>
        
        </div>
      );
    // } else {
    //   productData = <h2>Sorry. Product doesn't exist</h2>;
    // }
    // return(
    //   <div>
    //     {productData}

    //   </div>
    // )
           */}



export default JournalDetail;
